function AnimateScrollGallery(){for(i in ScrollGallery){const e=-1*ScrollGallery[i].offsetWidth+ScrollGallery[i].parentNode.clientWidth;ScrollGallery[i].style.translate=e+"px"}}function HamburgerToggle(){if(parseInt(window.getComputedStyle(document.body).width)>460)return;const e=window.getComputedStyle(HeaderItems[0]).display;if("none"==e){HamburgerBtn.setAttribute("src","./Assets/Imgs/Ham_Open.webp");for(let e=0;e<HeaderItems.length;e++)HeaderItems[e].style.display="flex";HamburgerClosed=!1}else{HamburgerBtn.setAttribute("src","./Assets/Imgs/Ham_Closed.webp");for(let e=0;e<HeaderItems.length;e++)HeaderItems[e].style.display="none";HamburgerClosed=!0}}let ScrollGallery=document.getElementsByClassName("ScrollGallery");const HeaderItems=document.getElementsByClassName("headerItems"),HamburgerBtn=document.getElementById("HamburgerMenu");AnimateScrollGallery();